import React, { Component } from 'react';
import { Route, Link, Switch } from 'react-router-dom';
import Cast from '../conteiner/cast/Cast';
import Reviews from '../reviews/Reviews';

import axios from 'axios';

export default class MovieDetailsPage extends Component {
  state = {
    poster_path: '',
    original_title: '',
    overview: '',
    genres: '',
    vote_average: null,
    id: null,
    movies: [],
    movie: null,
  };

  async componentDidMount() {
    const { movieID } = this.props.match.params;
    const res = await axios.get(
      `${process.env.REACT_APP_DATA_BASEURL}/movie/${movieID}?api_key=${process.env.REACT_APP_KEY}`,
    );

    this.setState({
      ...res.data,
      genres: res.data.genres.find(genre => genre),
      poster_path: `https://www.themoviedb.org/t/p/w600_and_h900_bestv2${res.data.poster_path}`,
      movies: res.data,
    });
  }

  render() {
    const { match } = this.props;
    const {
      poster_path,
      original_title,
      overview,
      genres,
      vote_average,
      id,
      movie,
    } = this.state;

    return (
      <>
        <img src={poster_path} alt="Photo title" />
        <h2>{original_title}</h2>
        <p>{genres.name}</p>
        <p>{vote_average}</p>
        <p>{overview}</p>

        <ul>
          <li>
            <Link to={`/movies/${this.props.match.params.movieID}/cast`}>
              cast
              <Cast movieID={this.props.match.params.movieID}></Cast>
            </Link>
          </li>
          <li>
            <Link to={`/movies/${this.props.match.params.movieID}/reviews`}>
              reviews
              <Reviews movieID={this.props.match.params.movieID}></Reviews>
            </Link>
          </li>
        </ul>

        <Route path="/movies/:movieID/cast" component={Cast}></Route>
        <Route path="/movies/:movieID/reviews" component={Reviews}></Route>
      </>
    );
  }
}

