import React, { Component } from 'react';
import axios from 'axios';

export default class MovieDetailsPage extends Component {
  state = {
    poster_path: '',
    original_title: '',
    overview: '',
    genres: '',
    vote_average: null,
    id: null,
  };

  async componentDidMount() {
    const { movieID } = this.props.match.params;
    const res = await axios.get(
      `${process.env.REACT_APP_DATA_BASEURL}/movie/${movieID}?api_key=${process.env.REACT_APP_KEY}`,
    );

    // res.data.genres.find(genre => console.log(genre.name));
    this.setState({
      ...res.data,
      genres: res.data.genres.find(genre => genre),
    });
  }

  render() {
    const {
      poster_path,
      original_title,
      overview,
      genres,
      vote_average,
    } = this.state;

    return (
      <>
        <img src={poster_path} alt="Photo title" />
        <h2>{original_title}</h2>
        {/* {this.state && <p>Title: {this.state.title}</p>} */}
        <p>{vote_average}</p>
        <p>{overview}</p>
        <p>{genres.name}</p>
      </>
    );
  }
}

