import React, { Component } from 'react';
import { Route, Link, Switch } from 'react-router-dom';
import Cast from '../conteiner/cast/Cast';
import axios from 'axios';

export default class MoviesPage extends Component {
  state = {
    searchQuery: '',
    query: '',
    movies: [],
  };

  async componentDidUpdate(prevProps, prevState) {
    if (prevState.query !== this.state.query) {
      const { query } = this.state;
      const res = await axios.get(
        `${process.env.REACT_APP_DATA_BASEURL}/search/movie?api_key=${process.env.REACT_APP_KEY}&query=${query}`,
      );
      this.setState({ movies: res.data.results });
    }
  }

  handlerInput = e => {
    this.setState({ searchQuery: e.target.value });
  };

  handlerSubmit = e => {
    e.preventDefault();
    this.setState({ query: this.state.searchQuery });
  };

  render() {
    const { searchQuery, movies } = this.state;
    const { match } = this.props;
    console.log(match);
    return (
      <>
        <form className="searchFom" onSubmit={this.handlerSubmit}>
          <input
            className="searchInput"
            type="text"
            autoComplete="off"
            autoFocus
            placeholder="movie name"
            aria-label="Search"
            value={searchQuery}
            onChange={this.handlerInput}
          />
          <button
            className="buttonForm"
            onSubmit={this.handlerSubmit}
            type="submit"
          >
            Search
          </button>
        </form>

        <ul>
          {movies.map(movie => (
            <li key={movie.id}>
              <Link to={`${this.props.url}/${movie.id}`}>{movie.title}</Link>
            </li>
          ))}
        </ul>
        <Route
          path={`${match.path}/:movieID`}
          render={props => {
            const movieID = Number(props.match.params.movieID);
            console.log(movieID);
            const movie = this.state.movies.find(({ id }) => id === movieID);
            console.log(movie);
            return movie && <Cast></Cast>;
          }}
        ></Route>
      </>
    );
  }
}
