import React, { Component } from 'react';
import axios from 'axios';

export default class Cast extends Component {
  state = {
    cast: null,
    movieID: this.props.moviID,
  };
  async componentDidMount() {
    console.log(this.state.movieID);
    const { movieID } = this.props.movieID;
    // console.log(movieID);
    const res = await axios.get(
      `${process.env.REACT_APP_DATA_BASEURL}/movie/${movieID}/credits?api_key=${process.env.REACT_APP_KEY}`,
    );

    this.setState({
      ...res.data,
      genres: res.data.genres.find(genre => genre),
      poster_path: `https://www.themoviedb.org/t/p/w600_and_h900_bestv2${res.data.poster_path}`,
      movies: res.data,
    });
  }

  render() {
