import React, { Component } from 'react';
import axios from 'axios';

export default class MovieDetailsPage extends Component {
  state = {
    movies: [],
  };

  async componentDidMount() {
    const { movieID } = this.props.match.params;
    console.log(movieID);
    const res = await axios.get(
      `${process.env.REACT_APP_DATA_BASEURL}/trending/movie/${movieID}?api_key=4fbdbd8abdbcde78896e194e86813212`,
    );
    console.log(res);
    this.setState({ ...res });
  }

  render() {
    const {
      backdrop_path,
      title,
      description,
      genres,
      popularity,
    } = this.state;
    console.log(title);
    return (
      <>
        <img src={backdrop_path} alt="Photo title" />
        <h2>{title}</h2>
        {/* {this.state && <p>Title: {this.state.title}</p>} */}
        <p>{popularity}</p>
        <p>{description}</p>
        <p>{genres}</p>
      </>
    );
  }
}

