import React, { Component } from 'react';
import { Route, Link, Switch } from 'react-router-dom';
import Cast from '../conteiner/cast/Cast';
// import MoviesPage from './MoviesPage';
import axios from 'axios';

export default class MovieDetailsPage extends Component {
  state = {
    poster_path: '',
    original_title: '',
    overview: '',
    genres: '',
    vote_average: null,
    id: null,
  };

  async componentDidMount() {
    const { movieID } = this.props.match.params;
    const res = await axios.get(
      `${process.env.REACT_APP_DATA_BASEURL}/movie/${movieID}?api_key=${process.env.REACT_APP_KEY}`,
    );

    this.setState({
      ...res.data,
      genres: res.data.genres.find(genre => genre),
      poster_path: `https://www.themoviedb.org/t/p/w600_and_h900_bestv2${res.data.poster_path}`,
    });
  }

  render() {
    const { match } = this.props;
    console.log(match);
    const {
      poster_path,
      original_title,
      overview,
      genres,
      vote_average,
    } = this.state;

    return (
      <>
        <img src={poster_path} alt="Photo title" />
        <h2>{original_title}</h2>
        <p>{genres.name}</p>
        <p>{vote_average}</p>
        <p>{overview}</p>
        <Route
          path={`${match.path}`}
          render={props => {
            const movieID = Number(match.params.movieID);
            console.log(movieID);
            const movie = this.state.find(({ id }) => id === movieID);
            console.log(movie);
            return movie && <Cast></Cast>;
          }}
        ></Route>
      </>
    );
  }
}

