import React, { Component } from 'react';
import axios from 'axios';

export default class MovieDetailsPage extends Component {
  state = {
    title: '',
    description: '',
    genres: '',
    backdrop_path: '',
  };

  async componentDidMount() {
    const { movieID } = this.props.match.params;
    console.log(movieID);
    const res = await axios.get(
      `${process.env.REACT_APP_DATA_BASE_BASEURL}/trending/movie/${movieID}?api_key=4fbdbd8abdbcde78896e194e86813212`,
    );
    this.setState({ ...res.data });
  }

  render() {
    const { backdrop_path, title, description, genres } = this.state;
    return (
      <>
        <img src={backdrop_path} alt="Photo title" />
        <h2>{title}</h2>
        {this.state.author && <p>Author: {this.state.author.name}</p>}
        <p>{description}</p>
        <p>{genres}</p>
      </>
    );
  }
}

