import React, { Component } from 'react';
import { Route, Link, Switch } from 'react-router-dom';
import axios from 'axios';
// import { Link } from 'react-router-dom';

export default class MoviesPage extends Component {
  // static propTypes = {
  //   onSubmit: PropTypes.func.isRequired,
  // };
  state = {
    searchQuery: '',
    query: '',
    movies: [],
  };

  componentDidUpdate(prevProps, prevState) {
    const prevQuery = prevState.searchQuery;
    const nextQuery = this.state.searchQuery;
    if (prevState.query !== this.state.query) {
      this.fetchMovie();
    }
    // console.log(this.state.movie);
  }

  fetchMovie = () => {
    // this.setState({ loading: true });
    const { searchQuery, query } = this.state;
    const res = axios
      .get(
        `${process.env.REACT_APP_DATA_BASEURL}/search/movie?api_key=${process.env.REACT_APP_KEY}&query=${query}`,
      )
      .then(res => {
        this.setState({ movies: res.data.results, searchQuery: query });
        console.log(res.data.results);
        console.log(this.state.movies);
      });
  };

  // handlerSearchFormSubmit = query => {
  //   this.setState({ searchQuery: query, movie: [] });
  // };

  // async componentDidUpdate(prevProps, prevState) {
  //   if (prevState.query !== this.state.query) {
  //     const res = await axios.get(
  //       `${process.env.REACT_APP_DATA_BASEURL}/search/movie?api_key=${process.env.REACT_APP_KEY}&query=${this.state.query}`,
  //     );
  //     this.setState({ movie: res.data.results });
  //     console.log(res.data.results);
  //     console.log(this.setStat);
  //   }
  // }

  handlerInput = e => {
    this.setState({ query: e.target.value });
    console.log(e.target.value);
  };

  handlerSubmit = e => {
    e.preventDefault();
    // this.handlerSearchFormSubmit(this.state.query);
    this.setState({ query: '', movies: [] });
  };

  render() {
    const { query, movies } = this.state;
    return (
      <>
        <input
          className="searchInput"
          type="text"
          autoComplete="off"
          autoFocus
          placeholder="movie name"
          aria-label="Search"
          value={query}
          onChange={this.handlerInput}
        />
        <button
          className="buttonForm"
          onSubmit={this.handlerSubmit}
          type="submit"
        >
          Search
        </button>

        <ul>
          {movies.map(movie => (
            <li key={movie.id}>
              <Link to={`/movie/${movie.id}`}>{movie.title}</Link>
            </li>
          ))}
        </ul>
      </>
    );
  }
}

