import React, { Component } from 'react';
import { Route, Link, Switch } from 'react-router-dom';
import Cast from '../cast/Cast';
import Reviews from '../reviews/Reviews';

import IconLabelButtons from '../button/ButtonBack';

import axios from 'axios';
import CardMovie from '../cardMovie/CardMovie';

export default class MovieDetailsPage extends Component {
  state = {
    poster_path: '',
    original_title: '',
    overview: '',
    genres: [],
    vote_average: null,
    movies: [],
    release_date: '',
  };

  async componentDidMount() {
    const { movieID } = this.props.match.params;
    const res = await axios.get(
      `${process.env.REACT_APP_DATA_BASEURL}/movie/${movieID}?api_key=${process.env.REACT_APP_KEY}`,
    );
    let movieGenres = [];
    res.data.genres.forEach(el => {
      movieGenres.push(' ' + el.name);
    });

    this.setState({
      ...res.data,
      genres: movieGenres,
      poster_path: `https://www.themoviedb.org/t/p/w600_and_h900_bestv2${res.data.poster_path}`,
      movies: res.data,
    });
  }

  render() {
    const { match } = this.props;
    console.log(match.url);
    const {
      poster_path,
      original_title,
      overview,
      genres,
      vote_average,
    } = this.state;
    const release_datere = this.state.release_date.substring(0, 4);
    return (
      <>
        <IconLabelButtons></IconLabelButtons>
        <CardMovie
          movieCard={this.state}
          release_datere={release_datere}
        ></CardMovie>
        <div className="cardMovie">
          <div>
            <img src={poster_path} alt="Photo title" />
          </div>
          <div>
            <h2>
              {original_title} ({release_datere})
            </h2>
            <div>
              <h3>Overview</h3>
              <p className="overview">{overview}</p>
            </div>
            <h3>Genres</h3>
            <p>{genres}</p>
            <p>{vote_average}</p>
          </div>
        </div>

        <ul>
          <li>
            <Link to={`${match.url}/cast`}>cast</Link>
          </li>
          <li>
            <Link to={`${match.url}/reviews`}>reviews</Link>
          </li>
        </ul>
        <Switch>
          <Route path={`${match.path}/cast`} component={Cast}></Route>
          <Route path={`${match.path}/reviews`} component={Reviews}></Route>
        </Switch>
      </>
    );
  }
}

