import React, { Component } from 'react';
import axios from 'axios';
// import { Link } from 'react-router-dom';

export default class MoviesPage extends Component {
  // static propTypes = {
  //   onSubmit: PropTypes.func.isRequired,
  // };
  state = {
    searchQuery: '',
    query: '',
    movie: [],
  };

  componentDidUpdate(prevProps, prevState) {
    const prevQuery = prevState.searchQuery;
    const nextQuery = this.state.searchQuery;
    if (prevQuery !== nextQuery) {
      this.fetchMovie();
    }
  }
  fetchMovie = () => {
    // this.setState({ loading: true });
    const { searchQuery } = this.state;
    const res = axios.get(
      `${process.env.REACT_APP_DATA_BASEURL}/search/movie?api_key=${process.env.REACT_APP_KEY}&query=${this.state.query}`,
    );
    this.setState({ movie: res.data.results });
  };

  handlerSearchFormSubmit = query => {
    // this.setState({ searchQuery: query, movie: [] });
  };

  // async componentDidUpdate(prevProps, prevState) {
  //   if (prevState.query !== this.state.query) {
  //     const res = await axios.get(
  //       `${process.env.REACT_APP_DATA_BASEURL}/search/movie?api_key=${process.env.REACT_APP_KEY}&query=${this.state.query}`,
  //     );
  //     this.setState({ movie: res.data.results });
  //     console.log(res.data.results);
  //     console.log(this.setStat);
  //   }
  // }

  handlerInput = e => {
    this.setState({ query: e.target.value });
  };

  handlerSubmit = e => {
    e.preventDefault();
    // this.props.onSubmit(this.state.query);
    this.setState({ query: '' });
  };

  render() {
    const { query } = this.state;
    return (
      <form className="searchFom" onSubmit={this.handlerSubmit}>
        <input
          className="searchInput"
          type="text"
          autoComplete="off"
          autoFocus
          placeholder="movie name"
          aria-label="Search"
          value={query}
          onChange={this.handlerInput}
        />
        <button className="buttonForm" type="submit">
          Search
        </button>
      </form>
    );
  }
}

