import axios from 'axios';

const getTrendingMovies = () => {
  try {
    const res = axios.get(
      `${process.env.REACT_APP_DATA_BASEURL}/trending/movie/day?api_key=${process.env.REACT_APP_KEY}`,
      this.setState({ movies: res.data.results }),
    );
    return res.data;
  } catch (error) {
    console.log(error);
  }
};

export { getTrendingMovies };
