import React from 'react';
import { Route, NavLink, Switch } from 'react-router-dom';
import HomeView from './view/HomeView';
import AuthorsView from './view/AuthorsView';
import BooksView from './view/BooksView';
import NotFound from './view/NotFound';
import BookDetailsView from './view/BookDetailsView';

export default function App() {
  return (
    <>
      <ul>
        <li>
          <NavLink
            exact
            to="/"
            className="NavLink"
            activeClassName="NavLink-active"
          >
            Home
          </NavLink>
        </li>
        <li>
          <NavLink
            to="/authors"
            className="NavLink"
            activeClassName="NavLink-active"
          >
            Autthors
          </NavLink>
        </li>
        <li>
          <NavLink
            to="/books"
            className="NavLink"
            activeClassName="NavLink-active"
          >
            Books
          </NavLink>
        </li>
      </ul>

      <Switch>
        <Route exact path="/" component={HomeView}></Route>
        <Route path="/authors" component={AuthorsView}></Route>
        <Route exact path="/books" component={BooksView}></Route>
        <Route path="/books/:bookID" component={BookDetailsView}></Route>
        <Route component={NotFound} />
      </Switch>
    </>
  );
}

