import React, { Component } from 'react';
import { Route, Link, Switch } from 'react-router-dom';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import MovieList from '../../movieList/MovieList';
import axios from 'axios';
import qs from 'query-string';
import style from './MoviesPage.scss';

export default class MoviesPage extends Component {
  state = {
    total_pages: 1,
    query: '',
    movies: [],
  };

  componentDidUpdate(prevProps) {
    if (prevProps.location.search !== this.props.location.search) {
      this.onQuerySearch(this.state.query);
    }

    //   const { query } = this.state;
    //   const res = await axios.get(
    //     `${process.env.REACT_APP_DATA_BASEURL}/search/movie?api_key=${process.env.REACT_APP_KEY}&query=${query}`,
    //   );
    //   this.setState({ movies: res.data.results });
    // }
  }
  componentDidMount() {
    const { query } = qs.parse(this.props.location.search);
    if (query) {
      this.onQuerySearch(query);
    }
  }

  onQuerySearch = async query => {
    const res = await axios.get(
      `${process.env.REACT_APP_DATA_BASEURL}/search/movie?api_key=${process.env.REACT_APP_KEY}&query=${query}`,
    );

    this.setState({
      movies: res.data.results,
      total_pages: res.data.total_pages,
    });
  };

  handlerInput = e => {
    if (!e.target.value) {
      this.setState({
        total_pages: 1,
      });
    }
    this.setState({ query: e.target.value });
  };

  handlerSubmit = e => {
    e.preventDefault();
    this.props.history.push({
      pathname: this.props.location.pathname,
      search: `query=${this.state.query}`,
    }); // this.setState({ query: this.state.searchQuery });
  };

  render() {
    const { query, movies, total_pages } = this.state;
    console.log(movies);
    return (
      <div className={style.box}>
        <form className={style.searchFom} onSubmit={this.handlerSubmit}>
          <TextField
            className={style.TextField}
            id="standard-search"
            label="Search field"
            type="search"
            autoComplete="off"
            autoFocus
            placeholder="movie name"
            aria-label="Search"
            value={query}
            onChange={this.handlerInput}
          />
          <Button
            variant="outlined"
            color="primary"
            className={style.buttonForm}
            // onSubmit={this.handlerSubmit}
            type="submit"
          >
            Search
          </Button>
        </form>
        <MovieList movies={this.state.movies}></MovieList>
      </div>
    );
  }
}
